import React, { useState } from 'react';
import AddUser from './Users/AddUser';

const USERS = [];

function App() {

  const [users, setUsers] = useState(USERS);

  const addUserHandler = (user) => {
    console.log(user);
    setUsers(
      prevUsers => {
        console.log(users);
        if(users.length === 0){
          return [user];
        }else{
          return [user, ...users];
        }
      }
    );
  };

  return (
    <div>
        <AddUser onAddUser = {addUserHandler}/>
        <UserList usersList = {users}/>
    </div>
  );
}

export default App;
