import React, { useState } from 'react';
import Card from '../UI/Card';
import styles from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModal from '../UI/ErrorModal';

function AddUser(props){

    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [error, setError] = useState();

    const usernameChangeHandler = (event) => {
        setUsername(event.target.value);
    };

    const ageChangeHandler = (event) => {
        setAge(event.target.value);
    };

    const onSubmitHandler = (event) => {
        // Essentially doesn't make the page reload
        // which is important as React doesn't reload the page.
        event.preventDefault();
        if (username.trim().length === 0 || age.trim().length === 0){
            setError({
                title: "Invalid Input!",
                message: "Please enter a valid name and age (non-empty values)"
            });
            return;
        }
        if (+age < 1){
            setError({
                title: "Invalid Age!",
                message: "Please enter a valid age (>0)"
            });
            return;
        }

        props.onAddUser(username, age);

        console.log(age, username);
        setUsername('');
        setAge('');

    };

    return (
        <div>
            <ErrorModal title="This"></ErrorModal>
            <Card className = {styles.input}>
                <form onSubmit={onSubmitHandler}>
                    <label htmlFor="username">Username</label>
                    <input id="username" type="text" value={username} onChange={usernameChangeHandler}></input>
                    <label htmlFor="age">Age (Years)</label>
                    <input id = "age" type="number" value={age} onChange={ageChangeHandler}></input>
                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        </div>
    );
};

export default AddUser
