import React, { useState, useRef } from 'react';
import Card from '../UI/Card';
import styles from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModal from '../UI/ErrorModal';
import Wrapper from '../Helpers/Wrapper';

function AddUser(props){

    const nameInputRef = useRef();
    const ageInputRef = useRef();

    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [error, setError] = useState();

    const usernameChangeHandler = (event) => {
        setUsername(event.target.value);
    };

    const ageChangeHandler = (event) => {
        setAge(event.target.value);
    };

    const onSubmitHandler = (event) => {
        // Essentially doesn't make the page reload
        // which is important as React doesn't reload the page.
        event.preventDefault();

        const enteredName = nameInputRef.current.value;
        const enteredAge = ageInputRef.current.value;

        if (enteredName.trim().length === 0 || enteredAge.trim().length === 0){
            setError({
                title: "Invalid Input!",
                message: "Please enter a valid name and age (non-empty values)"
            });
            return;
        }
        if (+enteredAge < 1){
            setError({
                title: "Invalid Age!",
                message: "Please enter a valid age (> 0)"
            });
            return;
        }

        props.onAddUser(enteredName, enteredAge);

        nameInputRef.current.value = '';
        ageInputRef.current.value = '';

    };

    const errorHandler = () => {
        setError(null);
    };

    return (
        <Wrapper>
            {error && <ErrorModal title={error.title} message={error.message} onConfirm={errorHandler}></ErrorModal>}
            <Card className = {styles.input}>
                <form onSubmit={onSubmitHandler}>
                    <label htmlFor="username">Username</label>
                    <input id="username" type="text" value={username} onChange={usernameChangeHandler} ref={nameInputRef}></input>
                    <label htmlFor="age">Age (Years)</label>
                    <input id = "age" type="number" value={age} onChange={ageChangeHandler} ref={ageInputRef}></input>
                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        </Wrapper>
    );
};

export default AddUser
